package no.nav.melosys.eessi.controller.dto

import org.apache.commons.lang3.StringUtils
import java.util.stream.Stream

enum class Adressetype(val adressetypeRina: String) {
    BOSTEDSADRESSE("bosted"), POSTADRESSE("opphold"), KONTAKTADRESSE("kontakt"), ANNET("annet");

    companion object {
        fun fraAdressetypeRina(adressetypeRina: String?): Adressetype {
            if (StringUtils.isEmpty(adressetypeRina)) {
                return ANNET
            }
            return Stream.of(*entries.toTypedArray())
                .filter { adressetype: Adressetype -> adressetype.adressetypeRina.equals(adressetypeRina, ignoreCase = true) }.findFirst().orElse(
                    ANNET
                )
        }
    }
}
